package br.com.crescer.holeinthewallapi.service.muro;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.junit.jupiter.MockitoExtension;

import java.io.IOException;
import java.net.URISyntaxException;

@ExtendWith(MockitoExtension.class)
class ConverterImagemResourceParaBase64ServiceTest {

    @InjectMocks
    private ConverterImagemResourceParaBase64Service converterImagemResourceParaBase64Service;

    @Test
    public void converterParaBase64() throws IOException, URISyntaxException {
        String fotoConvertidaEmBase64 = converterImagemResourceParaBase64Service.converterParaBase64("stick/stick1.png");

        String base64 = "data:image/png;base64,";

        Assertions.assertEquals(base64, fotoConvertidaEmBase64);
    }

}
